import React from 'react';
import '../styles/HomePage.css'; // Import the CSS file

const HomePage = () => {
  // Import images dynamically from the posters folder
  const images = [
    require('../assets/posters/poster1.jpg'),
    require('../assets/posters/poster2.jpg'),
    require('../assets/posters/poster3.jpg'),
    require('../assets/posters/poster4.jpg'),
    require('../assets/posters/poster5.jpg'),
  ];

  return (
    <div className="home-container">
      <h1 className="home-title">Welcome to Our Store</h1>
      <div className="image-grid">
        {images.map((image, index) => (
          <div className="image-item" key={index}>
            <img src={image} alt={`Poster ${index + 1}`} className="poster-image" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
